import React, { useContext, useState } from "react";
import Modal from "../UI/Modal";
import CartItem from "./CartItem";
import classes from "./Cart.module.css";
import CartContext from "../../store/cart-context";
import Checkout from "./Checkout";

const Bill = ({ orderDetails }) => {
  const { orderId, name, phoneNumber, roomNumber, time, date, totalAmount, numberOfItems } = orderDetails;
  return (
    <div className={classes.bill}>
      <h2>Order Bill</h2>
      <ul>
        <li>Name: {name}</li>
        <li>Phone Number: {phoneNumber}</li>
        <li>Room Number: {roomNumber}</li>
        <li>Order ID: {orderId}</li>
        <li>Time: {time}</li>
        <li>Date: {date}</li>
        <li>Number of Items: {numberOfItems}</li>
        <li>Total Amount: {totalAmount}</li>
      </ul>
    </div>
  );
};


const Cart = (props) => {
  const [isCheckout, setIsCheckout] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [didSubmit, setDidSubmit] = useState(false);
  const [showConfirmAlert, setShowConfirmAlert] = useState(false);
  const [orderDetails, setOrderDetails] = useState(null);
  const cartCtx = useContext(CartContext);

  const totalAmount = `Rs. ${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  };

  const orderHandler = () => {
    setIsCheckout(true);
  };

  const generateOrderId = () => {
    const timestamp = Date.now();
    const randomPortion = Math.floor(Math.random() * 1000);
    return `${timestamp}${randomPortion}`;
  };

  const submitOrderHandler = async (userData) => {
    const orderId = generateOrderId();
    const now = new Date();
    const numberOfItems = cartCtx.items.reduce((total, item) => total + item.amount, 0);
    const orderDetails = {
      orderId,
      name: userData.name,
      phoneNumber: userData.mobileNumber,
      roomNumber: userData.roomNumber,
      time: `${now.getHours()}:${now.getMinutes()}`,
      date: `${now.getDate()}/${now.getMonth() + 1}/${now.getFullYear()}`,
      totalAmount,
      numberOfItems,
    };
  
    setIsSubmitting(true);
    try {
        await fetch("https://react-http-94dfa-default-rtdb.firebaseio.com/orders.json", {
            method: "POST",
            body: JSON.stringify({
                orderId,
                user: userData,
                orderedItems: cartCtx.items,
            }),
            headers: {
                'Content-Type': 'application/json'
            },
        });
        setIsSubmitting(false);
        setDidSubmit(true);
        setOrderDetails(orderDetails);
        cartCtx.clearCart();
    } catch (error) {
        console.error('Failed to submit order:', error);
        setIsSubmitting(false);
    }
  };
  

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );

  const modalActions = (
    <div className={classes.actions}>
      <button className={classes["button--alt"]} onClick={props.onClose}>
        Close
      </button>
      {hasItems && (
        <button className={classes.button} onClick={orderHandler}>
          Order
        </button>
      )}
    </div>
  );

  const cartModalContent = (
    <React.Fragment>
      {!isCheckout && cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      {isCheckout && (
        <Checkout onConfirm={submitOrderHandler} onCancel={props.onClose} />
      )}
      {!isCheckout && modalActions}
    </React.Fragment>
  );

  const didSubmitModalContent = (
    <React.Fragment>
      <p>Successfully sent the order!</p>
      {orderDetails && <Bill orderDetails={orderDetails} />}
      <div className={classes.actions}>
        <button className={classes.button} onClick={props.onClose}>
          Close
        </button>
      </div>
    </React.Fragment>
  );

  return (
    <Modal onClose={props.onClose}>
      {!isSubmitting && !didSubmit && cartModalContent}
      {isSubmitting && <p>Sending order data...</p>}
      {!isSubmitting && didSubmit && didSubmitModalContent}
      {showConfirmAlert && <p>Your order is coming shortly!</p>}
    </Modal>
  );
};

export default Cart;
