// src/components/UI/OrderForm.js
import React, { useState } from 'react';
import orderService from '../services/orderService';

const OrderForm = () => {
    const [orderData, setOrderData] = useState({
        name: '',
        mobile_number: '',
        room_number: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setOrderData({ ...orderData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        orderService.createOrder(orderData)
            .then(response => {
                console.log(response.data);
                // Reset form or show success message
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="name" placeholder="Name" value={orderData.name} onChange={handleChange} required />
            <input type="text" name="mobile_number" placeholder="Mobile Number" value={orderData.mobile_number} onChange={handleChange} required />
            <input type="text" name="room_number" placeholder="Room Number" value={orderData.room_number} onChange={handleChange} required />
            <button type="submit">Submit</button>
        </form>
    );
};

export default OrderForm;
