import classes from './Checkout.module.css';
import { useRef, useState } from 'react';
import React from 'react';

const isNotEmpty = value => value.trim() !== "";  // True if not empty
const hasFiveChars = value => value.trim().length <= 5;  // True if has exactly 5 chars

const Checkout = (props) => {
  const [formInputValidity, setFormInputValidity] = useState({
    name: true,
    mobileNumber: true,
    roomNumber: true,
  });

  const [orderConfirmed, setOrderConfirmed] = useState(false);
  const [orderDetails, setOrderDetails] = useState({});

  const nameInputRef = useRef();
  const mobileNumberInputRef = useRef();
  const roomNumberInputRef = useRef();

  const confirmHandler = (event) => {
    event.preventDefault();

    const enteredName = nameInputRef.current.value;
    const enteredMobileNumber = mobileNumberInputRef.current.value;
    const enteredRoomNumber = roomNumberInputRef.current.value;

    const enteredNameIsValid = isNotEmpty(enteredName);
    const enteredMobileNumberIsValid = isNotEmpty(enteredMobileNumber);
    const enteredRoomNumberIsValid = hasFiveChars(enteredRoomNumber);

    setFormInputValidity({
      name: enteredNameIsValid,
      mobileNumber: enteredMobileNumberIsValid,
      roomNumber: enteredRoomNumberIsValid
    });

    const formIsValid = enteredNameIsValid && enteredMobileNumberIsValid && enteredRoomNumberIsValid;

    if (!formIsValid) {
      return;
    }

    // Assuming some order ID and price calculations here
    const orderId = Math.floor(Math.random() * 100000);
    const estimatedTime = '15 minutes';
    const price = '25 USD';  // This should be calculated based on actual data

    setOrderDetails({
      name: enteredName,
      mobileNumber: enteredMobileNumber,
      roomNumber: enteredRoomNumber,
      orderId,
      estimatedTime,
      price
    });

    setOrderConfirmed(true);
  };

  return (
    <div>
      {!orderConfirmed ? (
        <form className={classes.form} onSubmit={confirmHandler}>
          <div className={`${classes.control} ${formInputValidity.name ? '' : classes.invalid}`}>
            <label htmlFor='name'>Your Name</label>
            <input ref={nameInputRef} type='text' id='name' />
            {!formInputValidity.name && <p>Please enter a valid name.</p>}
          </div>
          <div className={`${classes.control} ${formInputValidity.mobileNumber ? '' : classes.invalid}`}>
            <label htmlFor='mobileNumber'>Mobile Number</label>
            <input ref={mobileNumberInputRef} type='text' id='mobileNumber' />
            {!formInputValidity.mobileNumber && <p>Please enter a valid mobile number.</p>}
          </div>
          <div className={`${classes.control} ${formInputValidity.roomNumber ? '' : classes.invalid}`}>
            <label htmlFor='roomNumber'>Room Number</label>
            <input ref={roomNumberInputRef} type='text' id='roomNumber' />
            {!formInputValidity.roomNumber && <p>Please enter a valid room number.</p>}
          </div>
          <div className={classes.actions}>
            <button type='button' onClick={props.onCancel}>
              Cancel
            </button>
            <button className={classes.submit}>Confirm</button>
          </div>
        </form>
      ) : (
        <div className={classes.bill}>
          <h2>Order Confirmation</h2>
          <p><strong>Order ID:</strong> {orderDetails.orderId}</p>
          <p><strong>Estimated Time:</strong> {orderDetails.estimatedTime}</p>
          <p><strong>Total Price:</strong> {orderDetails.price}</p>
          <p><strong>Name:</strong> {orderDetails.name}</p>
          <p><strong>Mobile Number:</strong> {orderDetails.mobileNumber}</p>
          <p><strong>Room Number:</strong> {orderDetails.roomNumber}</p>
        </div>
      )}
    </div>
  );
};

export default Checkout;
