import React, { useRef, useState } from 'react';
import classes from './Checkout.module.css';

const isNotEmpty = value => value.trim() !== "";  // True if not empty
const hasFiveChars = value => value.trim().length === 5;  // True if has exactly 5 characters

const Checkout = (props) => {
  const [formValues, setFormValues] = useState({
    name: '',
    mobileNumber: '',
    roomNumber: ''
  });
  const [formInputValidity, setFormInputValidity] = useState({
    name: false,
    mobileNumber: false,
    roomNumber: false
  });

  const formIsValid = formInputValidity.name && formInputValidity.mobileNumber && formInputValidity.roomNumber;

  const inputChangeHandler = (event) => {
    const { id, value } = event.target;

    const updatedValues = { ...formValues, [id]: value };
    const updatedValidities = {
      ...formInputValidity,
      name: isNotEmpty(updatedValues.name),
      mobileNumber: isNotEmpty(updatedValues.mobileNumber),
      roomNumber: hasFiveChars(updatedValues.roomNumber)
    };

    setFormValues(updatedValues);
    setFormInputValidity(updatedValidities);
  };

  const confirmHandler = (event) => {
    event.preventDefault();
    if (!formIsValid) return;

    console.log('Submitting form:', formValues);
    fetch('http://localhost:3001/checkout', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        name: formValues.name,
        mobileNumber: formValues.mobileNumber,
        roomNumber: formValues.roomNumber
      })
    })
    .then(response => response.json())
    .then(data => console.log("Data submitted:", data))
    .catch(error => console.error('Error:', error));
  };

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={`${classes.control} ${formInputValidity.name ? '' : classes.invalid}`}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' value={formValues.name} onChange={inputChangeHandler} />
        {!formInputValidity.name && <p>Please enter a valid name.</p>}
      </div>
      <div className={`${classes.control} ${formInputValidity.mobileNumber ? '' : classes.invalid}`}>
        <label htmlFor='mobileNumber'>Mobile Number</label>
        <input type='text' id='mobileNumber' value={formValues.mobileNumber} onChange={inputChangeHandler} />
        {!formInputValidity.mobileNumber && <p>Please enter a valid mobile number.</p>}
      </div>
      <div className={`${classes.control} ${formInputValidity.roomNumber ? '' : classes.invalid}`}>
        <label htmlFor='roomNumber'>Room Number</label>
        <input type='text' id='roomNumber' value={formValues.roomNumber} onChange={inputChangeHandler} />
        {!formInputValidity.roomNumber && <p>Room number must be exactly 5 characters.</p>}
      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.onCancel}>
          Cancel
        </button>
        <button type="submit" className={classes.submit} disabled={!formIsValid}>
          Confirm
        </button>
      </div>
    </form>
  );
};

export default Checkout;
