import React, { useState, useEffect } from 'react';
import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [meals, setMeals] = useState([]);  // Define the meals state

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/meals`, {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_API_KEY}`
        }
      });
      const responseData = await response.json();
      setMeals(responseData);  // Set the meals state
    };

    fetchMeals();
  }, []);

  return (
    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}
      <Header onShowCart={showCartHandler} />
      <main>
        <Meals items={meals} />  {/* Pass the meals state to Meals */}
      </main>
    </CartProvider>
  );
}

export default App;
