import React, { useState } from 'react';
import orderService from '../services/orderService';

const OrderForm = () => {
    const [orderData, setOrderData] = useState({
        orderId: '',
        mobileNumber: '',
        name: '',
        roomNo: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setOrderData({ ...orderData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        orderService.createOrder(orderData)
            .then(response => {
                console.log(response.data);
                // Reset form or show success message
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="orderId" placeholder="Order ID" value={orderData.orderId} onChange={handleChange} required />
            <input type="text" name="mobileNumber" placeholder="Mobile Number" value={orderData.mobileNumber} onChange={handleChange} required />
            <input type="text" name="name" placeholder="Name" value={orderData.name} onChange={handleChange} required />
            <input type="text" name="roomNo" placeholder="Room Number" value={orderData.roomNo} onChange={handleChange} required />
            <button type="submit">Submit</button>
        </form>
    );
};

export default OrderForm;
